import React, {Component} from 'react';

export default class ItemStatusFilter extends Component{

    state = {
        done: "done",
        active: "active",
        all: "all"
    }

    onSearchChange = (e) => {
        const term = e.target.value;
        this.setState({ term });
        this.props.onSearchChange(term);
    };

    all = (e) => {
        e.preventDefault();
        const all = "";
        this.props.onAll(all);
    }

    onlyActive = (e) => {
        e.preventDefault();
        const active = "active";
        this.props.onActive(active);
    }

    onlyDone = (e) => {
        e.preventDefault();
        const done = "done";
       this.props.onDone(done);
    }




    render() {
        return(



            <div className="btn-group">
                <button
                    type="button"
                        className="btn btn-info all"
                        onClick={this.all}
                >
                    All
                </button>


                <button
                    type="button"
                    className="btn btn-outline-secondary act"
                    onClick={this.onlyActive}
                >
                    Active
                </button>


                <button
                    type="button"
                    className="btn btn-outline-secondary done"
                    onClick={this.onlyDone}
                >
                    Done
                </button>


            </div>

        )
    }

}
