    }

    onToggleDone = (id) => {

    }

render()
{

    return (
        <div className="container main">
            <AppHeader/>
            <SearchPanel/>
            <TodoList todos={this.state.todoData}
                      onDeleted={this.deleteItem}
            />
            <AddTodoListItem newTextItem={this.addItem}/>
        </div>


    )
}
}
