import React, {Component} from 'react';

export default class ItemStatusFilter extends Component{

    state = {
        done: "done",
        active: "active"
    }

    onSearchChange = (e) => {
        const term = e.target.value;
        this.setState({ term });
        this.props.onSearchChange(term);
    };

    all = (event) => {
        const term = "";
        return this.props.todoData();
    }

    onlyActive = (event) => {
        const term = "active";
        this.props.onlyActive(this.state.active);
    }

    onlyDone = (event) => {
        const term = "done";
       this.props.onlyDone();
    }




    render() {
        return(



            <div className="btn-group">
                <button
                    type="button"
                        className="btn btn-info all"
                        onClick={this.all}
                >
                    All
                </button>


                <button
                    type="button"
                    className="btn btn-outline-secondary act"
                    onClick={this.onlyActive}
                >
                    Active
                </button>


                <button
                    type="button"
                    className="btn btn-outline-secondary done"
                    onClick={this.onlyDone}
                >
                    Done
                </button>


            </div>

        )
    }

}
