import React, {Component} from 'react';

export default class ItemStatusFilter extends Component{

    state = {
        done: "done",
        active: "active"
    }

    all = (event) => {
        return this.props.todoData();
    }

    onlyActive = (event) => {
        return this.props.onlyActive;
    }

    onlyDone = (event) => {
        return this.props.onlyDone;
    }




    render() {
        return(



            <div className="btn-group">
                <button
                    type="button"
                        className="btn btn-info all"
                        onClick={this.all}
                >
                    All
                </button>


                <button
                    type="button"
                    className="btn btn-outline-secondary act"
                    onClick={this.onlyActive}
                >
                    Active
                </button>


                <button
                    type="button"
                    className="btn btn-outline-secondary done"
                    onClick={this.onlyDone}
                >
                    Done
                </button>


            </div>

        )
    }

}
